window.addEventListener("DOMContentLoaded",()=>{navBg(),navLink(),addBr(),stikyDrone(),hideCollapse(),close(),year()}),window.addEventListener("scroll",()=>{stikyDrone(),navBg()});const navLink=()=>{document.querySelectorAll("li>a:not([href='./'])").forEach(e=>{e.addEventListener("click",function(e){if(location.pathname.replace(/^\//,"")==this.pathname.replace(/^\//,"")&&location.hostname==this.hostname){var t=$(this.hash);(t=t.length?t:$("[name="+this.hash.slice(1)+"]")).length&&(e.preventDefault(),$("html, body").animate({scrollTop:t.offset().top},1e3))}})})},navBg=()=>{const e=document.querySelector("nav.navbar"),t=$(window).scrollTop();e.classList&&t<e.offsetHeight?e.classList.remove("scrolled"):e.classList.add("scrolled")},stikyDrone=()=>{const e=document.querySelector("nav.navbar");document.querySelector("#about h3").style.cssText="top: "+e.offsetHeight+"px;"},addBr=()=>{const e=document.querySelectorAll("h6 > small");for(let t=0;t<e.length;t++){let o=e[t].textContent.split(" ");o[0]=o[0]+"<br />",e[t].innerHTML=o.join(" ")}},hideCollapse=()=>{document.querySelectorAll(".nav-link").forEach(e=>{e.addEventListener("click",()=>{$(".collapse").collapse("hide")})})},close=()=>{document.querySelector(".btn-close").addEventListener("click",()=>{document.querySelector(".navbar-collapse").classList.remove("show")})},year=()=>{document.querySelector(".year").insertAdjacentHTML("beforeend",(new Date).getFullYear())};$("body").scrollspy({target:"nav",offset:60});